func validateBarChartRender(t *testing.T, p *Painter, opt BarChartOption, expectedResult string) {
	t.Helper()

	err := p.BarChart(opt)
	require.NoError(t, err)
	data, err := p.Bytes()
	require.NoError(t, err)
	assertEqualSVG(t, expectedResult, data)
}
