func validateLineChartRender(t *testing.T, p *Painter, opt LineChartOption, expectedResult string) {
	t.Helper()

	err := p.LineChart(opt)
	require.NoError(t, err)
	data, err := p.Bytes()
	require.NoError(t, err)
	assertEqualSVG(t, expectedResult, data)
}
