func makeBasicPieChartOption() PieChartOption {
	values := []float64{
		1048,
		735,
		580,
		484,
		300,
	}
	return PieChartOption{
		SeriesList: NewPieSeriesList(values, PieSeriesOption{
			Label: SeriesLabel{Show: true},
		}),
		Title: TitleOption{
			Text:    "Rainfall vs Evaporation",
			Subtext: "Fake Data",
			Left:    PositionCenter,
		},
		Padding: Box{
			Top:    20,
			Right:  20,
			Bottom: 20,
			Left:   20,
		},
		Legend: LegendOption{
			Orient: OrientVertical,
			Data: []string{
				"Search Engine",
				"Direct",
				"Email",
				"Union Ads",
				"Video Ads",
			},
			Left: PositionLeft,
		},
	}
}

